import Header from './components/Header'
import Footer from './components/Footer'
import Tasks from './components/Tasks'
import { useState } from 'react'

class Task {
  constructor(id, text, day, reminder){
      this.id = id;
      this.text = text;
      this.day = day;
      this.reminder = reminder;
  }
}

function App() {

  let [tasks, setTasks] = useState([
    new Task(1,"Doctors Appointment", "Dec 5th at 2:30pm", true),
    new Task(2,"Meeting at School", "Dec 12th at 1:30pm", true),
    new Task(3,"Food Shopping", "Dec 14th at 2:30pm", false)
  ]);
  let deleteTask = (id)=>{
    // setTasks((id)=>{
    //    let idx = -1;
    //    for(let i = 0; i < tasks.length; i++) if(id == tasks[i].id) idx = i;
    //    tasks.splice(idx,1);
    //    return tasks;
    // });
    setTasks(()=>{
      let idx = -1;
      for(let i = 0; i < tasks.length; i++) if(id == tasks[i].id) idx = i;
      let newTasks = tasks;
      newTasks.splice(idx,1);
      console.log(newTasks);
      return newTasks;
    });
  };
  //let name = "Quang";
  return (
    <div className="container">
      <Header />
      {/* <h2>Hello {name} from React</h2> */}
      <Tasks tasks = {tasks} onDelete = {deleteTask}/>
      <Footer name = "Quang"/>
    </div>
  );
}

export default App;
