import React from 'react'
import PropTypes from 'prop-types'
import { FaTimes } from 'react-icons/fa'

function Task(props) {
    let color = props.task.reminder ? "green" : "white";
    let border = props.task.reminder ? "solid" : "none";
    return (
        <div className="task" style={{cursor:'pointer', borderStyle : border, borderColor : color}} onClick={()=>props.onToggleReminder(props.task.id)}>
            <h3>{props.task.text} <FaTimes onClick={()=> props.onDelete(props.task.id)} style={{color:"red", cursor:"pointer"}}/></h3>
            <p>{props.task.day}</p>
        </div>
    )
}

export default Task
